mapper.select:
select

from t_box_device_info d
where 1=1

	and d.id = #{id,jdbcType=VARCHAR}


	and d.factory_code = #{factoryCode,jdbcType=VARCHAR}


	AND d.box_model = #{boxModel,jdbcType=VARCHAR}


	AND d.device_name = #{deviceName,jdbcType=VARCHAR}

mapper.insert:
insert into t_box_device_info (id, factory_code, box_model, device_name, remark
  )
values (#{id,jdbcType=VARCHAR}, #{factoryCode,jdbcType=VARCHAR}, #{boxModel,jdbcType=VARCHAR}, #{deviceName,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

mapper.update:
update t_box_device_info
set factory_code = #{factoryCode,jdbcType=VARCHAR},
  box_model = #{boxModel,jdbcType=VARCHAR},
  device_name = #{deviceName,jdbcType=VARCHAR},
  remark = #{remark,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
 select 
 d.id, d.factory_code factoryCode, d.box_model  boxModel, d.device_name deviceName, d.remark, f.factory_name factoryName 
from t_box_device_info d, t_box_factory_info f 
where d.factory_code = f.factory_code 


    and d.id = #{id,jdbcType=VARCHAR}


    and d.factory_code = #{factoryCode,jdbcType=VARCHAR}


    AND d.box_model = #{boxModel,jdbcType=VARCHAR}


    AND d.device_name = #{deviceName,jdbcType=VARCHAR}
