update.foreach:
update t_diagnose_threshold_value set

	min_threshold_value = #{item.minThresholdValue,jdbcType=VARCHAR}


	,


	max_threshold_value = #{item.maxThresholdValue,jdbcType=VARCHAR}

where diagnose_type = #{item.diagnoseType,jdbcType=INTEGER}
and equipment_type = #{item.equipmentType,jdbcType=INTEGER}
and threshold_name = #{item.thresholdName,jdbcType=VARCHAR}

mapper.select:
select

from t_diagnose_threshold_value
where
diagnose_type = #{diagnoseType,jdbcType=INTEGER}
and
equipment_type = #{equipmentType,jdbcType=INTEGER}

mapper.update:
update t_diagnose_threshold_value
set
threshold_name =
#{thresholdName,jdbcType=VARCHAR},
threshold_value =
#{thresholdValue,jdbcType=VARCHAR}
where
diagnose_type = #{diagnoseType,jdbcType=INTEGER}
and
equipment_type = #{equipmentType,jdbcType=INTEGER}
