mapper.select:
select

from t_factory_info where 1=1

	and id = #{id,jdbcType=VARCHAR}


	and factory_code = #{factoryCode,jdbcType=CHAR}


	and factory_name like CONCAT('%',#{factoryName,jdbcType=CHAR},'%')


	and manufacturer_id = #{manufacturerId,jdbcType=VARCHAR}

select

from t_factory_info where 1=1

	and factory_code like CONCAT('%',#{ouicode,jdbcType=VARCHAR},'%')


	and factory_name like CONCAT('%',#{ouiName,jdbcType=VARCHAR},'%')


	and manufacturer_id = #{manufacturerId,jdbcType=VARCHAR}

order by factory_code asc limit #{lbound,jdbcType=INTEGER},#{mbound,jdbcType=INTEGER}

