mapper.select:
SELECT count(*) FROM t_firmware_upgrade_time
from t_firmware_upgrade_time
where upgrade_task_id = #{upgradeTaskId,jdbcType=VARCHAR}

mapper.update:
update t_firmware_upgrade_time
set upgrade_task_id = #{upgradeTaskId,jdbcType=VARCHAR},
  task_start_time = #{taskStartTime,jdbcType=INTEGER},
  task_end_time = #{taskEndTime,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}
