mapper.update:
update t_gateway_backup_file_task_detail
set gateway_id = #{gatewayId,jdbcType=VARCHAR},
  task_id = #{taskId,jdbcType=VARCHAR},
  backup_file_id = #{backupFileId,jdbcType=VARCHAR},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

update t_gateway_backup_file_task_detail
set status = #{status,jdbcType=INTEGER}
where backup_file_id = #{backupFileId,jdbcType=VARCHAR}

mapper.select:
select Count(1)
from t_gateway_backup_file_task_detail
where task_id = #{taskId,jdbcType=VARCHAR}
AND   gateway_id = #{gatewayId,jdbcType=VARCHAR}
