mapper.select:
SELECT id, business_code_boss, gateway_uuid, open_status, order_no FROM t_gateway_business_open_detail
WHERE 1=1

  AND business_code_boss = #{businessCodeBoss,jdbcType=VARCHAR}


  AND gateway_uuid = #{gatewayUuid,jdbcType=VARCHAR}


  AND open_status = #{openStatus,jdbcType=VARCHAR}


  AND order_no = #{orderNo,jdbcType=VARCHAR}

SELECT a.business_code_boss,a.business_name_boss,b.gateway_uuid,b.open_status
FROM t_business_code a LEFT JOIN t_gateway_business_open_detail b
ON a.business_code_boss = b.business_code_boss and b.gateway_uuid= #{gatewayUuid,jdbcType=VARCHAR}
