mapper.select:
SELECT   id, hd_version, device_model, device_id, create_time,description FROM t_hd_version_info
WHERE 1=1

  and a.id =  #{id,jdbcType=VARCHAR}


  and hd_version = #{hdVersion,jdbcType=VARCHAR}


  and device_model = #{deviceModel,jdbcType=VARCHAR}


  and device_id = #{deviceId,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=VARCHAR}

 SELECT mi.id AS manufacturerID,mi.make_name AS manufacturerName,
d.device_factory  AS factoryID,m.manufacturer_name AS factoryName,a.device_id AS deviceId,
a.device_model AS  deviceModel,a.hd_version AS hdVersion,a.id AS hdVersionId,
a.description AS description
FROM t_hd_version_info a, t_device_info d , t_manufacturer_info m, t_make_info mi
WHERE a.device_id = d.id AND d.device_factory = m.id AND m.make_id = mi.id

  AND a.id = #{hdVersionId,jdbcType=VARCHAR}


  AND a.hd_version = #{hdVersion,jdbcType=VARCHAR}


  AND a.device_model = #{deviceModel,jdbcType=VARCHAR}


  AND a.device_id = #{deviceId,jdbcType=VARCHAR}


   AND d.device_factory IN (SELECT id FROM t_manufacturer_info WHERE make_id = #{manufacturerID,jdbcType=VARCHAR})


  AND d.device_factory = #{factoryID,jdbcType=VARCHAR}

ORDER BY a.create_time DESC
