mapper.select:
select

from t_manufacturer_info where 1=1

	and id = #{id,jdbcType=VARCHAR}


	and manufacturer_name like CONCAT('%',#{manufacturerName,jdbcType=VARCHAR},'%')


	and code like CONCAT('%',#{code,jdbcType=VARCHAR},'%')


	and make_id = #{makeId,jdbcType=VARCHAR}

order by code asc limit #{lbound,jdbcType=INTEGER},#{mbound,jdbcType=INTEGER}

